  setMenus([<div id='clickMenu' style={clickMenuStyle}>
    <div className='menu-option' id="ch1" onClick={()=>checkAns('ch1')}>Character 1</div>
    <div className='menu-option' id="ch2" onClick={()=>checkAns('ch2')}>Character 2</div>
    <div className='menu-option' id="ch3" onClick={()=>checkAns('ch3')}>Character 3</div>
  </div>])
}



  return (
    <div className="App">
      <div className='menu'>
        <div className='timer'>
          {minutes}:{seconds}
        </div>
        <div className="buttons">


        </div>
        <div className="scoreboard">
          {position.x}:{position.y} 
        </div>

      </div>
      <img src='Full.jpg' alt='background' id='full-image' ref={ref} onClick={handleClick}></img>
      {menus}
    </div>
  );
}

export default App;
